import React, { Component } from 'react'
import { Container, Row, Col } from 'reactstrap'
// import ModalForm from './ModalUser'
// import DataTable from './DataTable'
import { CSVLink } from "react-csv"
import axios from 'axios'
import "./HomePage.css"

class HomePage extends Component {
  state = {
    items: [],
    isOpenModalUser: false,
    isOpenEditUser: false,
  }

  getItems() {
    axios.get('http://localhost:8000/home/data')
      .then(response => {
        console.log(response)
        this.setState({
          items: response.data.data,
        })
      }
      )
      .catch(function (error) {
        console.log(error);
      });
  }



  componentDidMount() {
    this.getItems()
  }

  render() {
    return (
      <div className='users-container'>
        <ModalUser
          isOpen={this.state.isOpenModalUser}
          toggleFromParent={this.handleToggleUserModal}
          createNewUser={this.handleCreateNewUser}
        />
        {/* {
          this.state.isOpenEditUser &&
          <ModalEditUser
            isOpen={this.state.isOpenEditUser}
            toggleFromParent={this.handleToggleEditModal}
            currentUser={this.state.userEdit}
            editUser={this.doEditUser}
          />
        } */}
        <div className="text-center">Manage users with Mavis</div>
        <div className='mx-1'>
          <button className='btn btn-primary px-3'
            onClick={() => { this.handleBtnAddNewUser() }}
          ><i className="fas fa-plus"></i>Add New User</button>
        </div>
        <div className='users-table mt-3 mx-1'>
          <table id="customers">
            <tbody>
              <tr>
                <th>Email</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Address</th>
                <th>Actions</th>
              </tr>
              {this.state.items && this.state.items.map((item, index) => {
                return (
                  <tr>
                    <td>{item.email}</td>
                    <td>{item.firstName}</td>
                    <td>{item.lastName}</td>
                    <td>{item.address}</td>
                    <td>
                      <button className='btn-edit' onClick={() => { this.handleEditUser(item) }}><i className="fas fa-pencil-alt">Edit</i></button>
                      <button className='btn-delete' onClick={() => { this.handleDeteleUser(item) }}><i className="fas fa-trash">Delete</i></button>
                    </td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        </div>

      </div >
    )
  }
}

export default HomePage
