import React, { Component } from 'react'
import axios from 'axios'
import './Login.css'
import { Navigate } from 'react-router-dom'


export default class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            isLogin: "false",
            errorCode: "",
        }
        // this.state = this.state.bind(this)
    }

    handleOnChangeUserName = (event) => {
        this.setState({
            username: event.target.value
        })
        console.log(this.state.username)
    }

    handleOnChangePassword = (event) => {
        this.setState({
            password: event.target.value
        })
        console.log(this.state.password)
    }


    handleButtonLogin = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8000/login', {
            username: this.state.username,
            password: this.state.password
        })
            .then(response => {
                if (response.status === 200) {
                    this.setState({ isLogin: "true" })
                    console.log(response)
                }
            }
            )
            .catch(function (error) {
                console.log(error);
            });
    }

    render() {
        return (
            <div>
                {/* <div className='background-login'>
                    <div className="background">
                        <div className="shape"></div>
                        <div className="shape"></div>
                    </div>
                    <form>
                        <h3>Login Here</h3>

                        <label for="username">Username</label>
                        <input type="text" placeholder="Username" id="username" onChange={(event) => { this.handleOnChangeUserName(event) }} />

                        <label for="password">Password</label>
                        <input type="password" placeholder="Password" id="password" onChange={(event) => { this.handleOnChangePassword(event) }} />

                        <button onClick={(event) => { this.handleButtonLogin(event) }}>Log In</button>
                        {this.state.isLogin === "true" && <Navigate to="/home" />}
                    </form>
                </div> */}
                <div className="limiter">
                    <div className="container-login100" >
                        <div className="wrap-login100 p-t-30 p-b-50">
                            <span className="login100-form-title p-b-41">
                                Account Login
                            </span>
                            <form className="login100-form validate-form p-b-33 p-t-5">

                                <div className="wrap-input100 validate-input" data-validate="Enter username">
                                    <input className="input100" type="text" name="username" placeholder="User name" />
                                    <span className="focus-input100" data-placeholder="&#xe82a;"></span>
                                </div>

                                <div className="wrap-input100 validate-input" data-validate="Enter password">
                                    <input className="input100" type="password" name="pass" placeholder="Password" />
                                    <span className="focus-input100" data-placeholder="&#xe80f;"></span>
                                </div>

                                <div className="container-login100-form-btn m-t-32">
                                    <button className="login100-form-btn">
                                        Login
                                    </button>
                                </div>

                            </form>
                        </div>
                    </div>
                </div>

            </div>
        )
    }
}
