import React, { Component } from 'react'
import { Table, Button } from 'reactstrap';
import ModalForm from './ModalForm'

class DataTable extends Component {

    deleteItem = id => {
        // let confirmDelete = window.confirm('Delete item forever?')
        // if (confirmDelete) {
        //     fetch('http://localhost:3000/crud', {
        //         method: 'delete',
        //         headers: {
        //             'Content-Type': 'application/json'
        //         },
        //         body: JSON.stringify({
        //             id
        //         })
        //     })
        //         .then(response => response.json())
        //         .then(item => {
        //             this.props.deleteItemFromState(id)
        //         })
        //         .catch(err => console.log(err))
        // }

    }

    render() {

        const items = this.props.items.map(item => {
            return (
                <tr key={item.id}>
                    <th >{item.userID}</th>
                    <td>{item.firstName}</td>
                    <td>{item.lastName}</td>
                    <td>{item.email}</td>
                    <td>{item.phoneNumber}</td>
                    <td>{item.address}</td>
                    <td>{item.position}</td>
                    <td>
                        <div style={{ width: "110px" }}>
                            <ModalForm className="btn-edit" buttonLabel="Edit" item={item} updateState={this.props.updateState} />
                            {' '}
                            <Button className="btn-delete" color="danger" onClick={() => this.deleteItem(item.id)}>Del</Button>
                        </div>
                    </td>
                </tr>
            )
        })

        return (
            <div className='users-table mt-3 mx-1'>
                <table id="customers">
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>First</th>
                            <th>Last</th>
                            <th>Email</th>
                            <th>Phone</th>
                            <th>Location</th>
                            <th>Hobby</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {items}
                    </tbody>
                </table>
            </div>
        )
    }
}

export default DataTable
