import React, { Component } from 'react'
import ModalUser from './ModalUser'
// import DataTable from './DataTable'
import axios from 'axios'
import "./HomePage.scss"

class HomePage extends Component {
  state = {
    items: [],
    isOpenModalUser: false,
    isOpenEditUser: false,
    userEdit: {}
  }

  getItems() {
    axios.get('http://localhost:8000/home/data')
      .then(response => {
        console.log(response)
        this.setState({
          items: response.data.data,
        })
      }
      )
      .catch(function (error) {
        console.log(error);
      });
  }




  async componentDidMount() {
    this.getItems();
  }

  getAllUsersFromReact = async () => {
    // let response = await getAllUsers("All");
    // if (response && response.errCode === 0) {
    //   this.setState({
    //     arrUsers: response.users
    //   })
    // }
  }
  handleBtnAddNewUser = () => {
    this.setState({
      isOpenModalUser: true
    })
  }

  handleToggleUserModal = () => {
    this.setState({
      isOpenModalUser: !this.state.isOpenModalUser
    })
  }

  handleToggleEditModal = () => {
    this.setState({
      isOpenEditUser: !this.state.isOpenEditUser
    })
  }

  handleCreateNewUser = async (data) => {
    axios.post('http://localhost:8000/home/addData', data)
      .then(response => {
        console.log(response)
        this.getItems();
        this.setState({
          isOpenModalUser: false,
        })
      }
      )
      .catch(function (error) {
        console.log(error);
      });
    // try {
    //   let response = await createNewUserService(data)
    //   if (response && response.errCode !== 0) {
    //     alert(response.errMessage);
    //   }
    //   else {
    //     await this.getAllUsersFromReact();
    //     this.setState({
    //       isOpenModalUser: false
    //     })
    //     emitter.emit('EVENT_CLEAR_MODAL_DATA', { 'id': 'your id' })
    //   }
    // }
    // catch (e) {
    //   console.log(e)
    // }
  }

  handleDeteleUser = async (user) => {
    axios.delete(`http://localhost:8000/home/deleteData/${user.userID}`)
      .then(response => {
        console.log(response)
        this.getItems();
        // this.setState({
        //   isOpenModalUser: false,
        // })
      }
      )
      .catch(function (error) {
        console.log(error);
      });
    // try {
    //   let response = await deteleUserService(user.id)
    //   if (response && response.errCode !== 0) {
    //     alert(response.errMessage);
    //   }
    //   else {
    //     await this.getAllUsersFromReact();
    //     this.setState({
    //       isOpenModalUser: false
    //     })
    //   }
    // }
    // catch (e) {
    //   console.log(e)
    // }
  }

  handleEditUser = async (user) => {
    // try {
    //   this.setState({
    //     isOpenEditUser: true,
    //     userEdit: user,
    //   })
    // }
    // catch (e) {
    //   console.log(e)
    // }
  }

  doEditUser = async (user) => {
    // try {
    //   let response = await editUserService(user)
    //   if (response && response.errCode !== 0) {
    //     alert(response.errMessage);
    //   }
    //   else {
    //     await this.getAllUsersFromReact();
    //     this.setState({
    //       isOpenEditUser: false
    //     })
    //   }
    // }
    // catch (e) {
    //   console.log(e)
    // }
  }

  render() {
    return (
      <div className='users-container'>
        <ModalUser
          isOpen={this.state.isOpenModalUser}
          toggleFromParent={this.handleToggleUserModal}
          createNewUser={this.handleCreateNewUser}
        />
        {/* {
          this.state.isOpenEditUser &&
          <ModalEditUser
            isOpen={this.state.isOpenEditUser}
            toggleFromParent={this.handleToggleEditModal}
            currentUser={this.state.userEdit}
            editUser={this.doEditUser}
          />
        } */}
        <div className="text-center">Manage users with Mavis</div>
        <div className='mx-1'>
          <button className='btn btn-primary px-3'
            onClick={() => { this.handleBtnAddNewUser() }}
          ><i className="fas fa-plus"></i>Add New User</button>
        </div>
        <div className='users-table mt-3 mx-1'>
          <table id="customers">
            <tbody>
              <tr>
                <th>Email</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Address</th>
                <th>Actions</th>
              </tr>
              {this.state.items && this.state.items.map((item, index) => {
                return (
                  <tr>
                    <td>{item.email}</td>
                    <td>{item.firstName}</td>
                    <td>{item.lastName}</td>
                    <td>{item.address}</td>
                    <td>
                      <button className='btn-edit' onClick={() => { this.handleEditUser(item) }}><i className="fas fa-pencil-alt"></i></button>
                      <button className='btn-delete' onClick={() => { this.handleDeteleUser(item) }}><i className="fas fa-trash"></i></button>
                    </td>
                  </tr>
                )
              })}
            </tbody>
          </table>
        </div>

      </div >
    )
  }
}

export default HomePage
