import React, { Component } from 'react'
import axios from 'axios'
import './Login.css'
import { Navigate } from 'react-router-dom'


export default class Login extends Component {

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: "",
            isLogin: "false",
        }
    }

    handleOnChangeUserName = (event) => {
        this.setState({
            username: event.target.value
        })
        console.log(this.state.username)
    }

    handleOnChangePassword = (event) => {
        this.setState({
            password: event.target.value
        })
        console.log(this.state.password)
    }


    handleButtonLogin = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8000/login', {
            firstName: 'Fred',
            username: this.state.username,
            password: this.state.password
        })
            .then(function (response) {
                // console.log(response.request.responseURL)
                console.log(this.state.isLogin)
                // window.location.href = 'http://localhost:8000/home';
                // this.setState({ isLogin: true })
            })
            .catch(function (error) {
                console.log(error);
            });
        // fetch('http://localhost:8000/login', {
        //     method: 'POST',
        //     headers: { 'Content-Type': 'application/json' },
        //     body: JSON.stringify({
        //         username: this.state.username,
        //         password: this.state.password
        //     }),
        // })
        //     .then((response) => console.log(response))
        //     .catch(error => console.log(error))
    }

    render() {
        return (
            <div>
                <div className="background">
                    <div className="shape"></div>
                    <div className="shape"></div>
                </div>
                <form>
                    <h3>Login Here</h3>

                    <label for="username">Username</label>
                    <input type="text" placeholder="Username" id="username" onChange={(event) => { this.handleOnChangeUserName(event) }} />

                    <label for="password">Password</label>
                    <input type="password" placeholder="Password" id="password" onChange={(event) => { this.handleOnChangePassword(event) }} />

                    <button onClick={(event) => { this.handleButtonLogin(event) }}>Log In</button>
                    {this.state.isLogin && <Navigate to="/home" />}
                </form>
            </div>
        )
    }
}
